mapper.select:
select

from IOT_SP_PUT_IN

	
		
		
			and ID = #{spPutIn.id,jdbcType=VARCHAR}
		
		
		
			and REQUEST_CODE = #{spPutIn.requestCode,jdbcType=VARCHAR}
		
		
		
			and PROPOSER like '%' ||
			#{spPutIn.proposer,jdbcType=VARCHAR} || '%'
		
		
		
			and TYPE = #{spPutIn.type,jdbcType=VARCHAR}
		
		
		
			and APPLICATION_STATUS =
			#{spPutIn.applicationStatus,jdbcType=VARCHAR}
		
		
		
			and OUT_PUT_STATUS = #{spPutIn.outPutStatus,jdbcType=VARCHAR}
		
		
		
			and REMARK like '%' ||
			#{spPutIn.remark,jdbcType=VARCHAR} || '%'
		
	
	
	
		and to_char(APPLICATION_DATE,'YYYY-MM-DD') >= #{beginDate,jdbcType=VARCHAR}
	
	
		and to_char(APPLICATION_DATE,'YYYY-MM-DD') <= #{endDate,jdbcType=VARCHAR}
